import {login} from "@/api/user"

const user = {
    state: {
        userName: '',
        nickName: '',
        token: ''
    },
    getters:{
    },
    mutations: {
        SAVE_USER_NAME(state, userName) {
            console.log(userName)
            state.userName = userName
        },
        SAVE_NICK_NAME(state, nickName) {
            state.nickName = nickName
        },
        SAVE_TOKEN(state, token) {
            state.token = token
        }
    },
    actions: {
        LOGIN({commit}, user) {
            login(user).then(req => {
                commit('SAVE_USER_NAME', req.data.userName)
                commit('SAVE_NICK_NAME', req.data.nickName)
                commit('SAVE_TOKEN', req.data.token)
            })
        }
    }
}

export default user
