import { onMounted } from "vue";
import { useRouter } from 'vue-router';
import storage from "@/utils/storage";
import { session } from "@/constant";
import { toMain } from "@/api/user";
export default {
  setup(__props, {
    expose
  }) {
    expose();
    const router = useRouter();

    const getPageName = () => {
      return new Promise((resolve, reject) => {
        toMain().then(resp => {
          console.log(resp);

          if (resp.status >= 200 && resp < 300) {
            resolve('main');
          } else {
            resolve('login');
          }
        }).catch(error => {
          reject(error);
        });
      });
    };

    onMounted(() => {
      if (storage.sessionKeyIsEmpty(session.SESSION_USER_TAG)) {
        console.log('检测到记录');
        getPageName().then(pageName => {
          console.log(pageName);
          router.push({
            name: pageName
          });
        }).catch(() => {
          router.push({
            name: 'login'
          });
        });
      } else {
        router.push({
          name: 'login'
        });
      } // router.push({name: 'login'})

    });
    const __returned__ = {
      router,
      getPageName,
      onMounted,
      useRouter,
      storage,
      session,
      toMain
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }

};
