import axios from "axios";

// 创建 axios 实例
const request = axios.create({
    // 请求url的公共部分
    baseURL: 'http://localhost:80/admin',
    // 超时时间
    timeout: 1000,
    // 设置 `content-type` ，规定前后端json格式通信
    headers: {'Content-Type': 'application/json;charset=UTF-8'}
})

export default request
