import {login} from "@/api/user"
import storage from "@/utils/storage";
import {actions, mutations} from "@/constant";

const user = {
    state: {
        userName: '',
        nickName: '',
        token: ''
    },
    getters:{
    },
    mutations: {
        [mutations.SAVE_USER_NAME](state, userName) {
            console.log(userName)
            state.userName = userName
        },
        [mutations.SAVE_NICK_NAME](state, nickName) {
            state.nickName = nickName
        },
        [mutations.SAVE_TOKEN](state, token) {
            state.token = token
        }
    },
    actions: {
        [actions.LOGIN]({commit}, user) {
            return new Promise((resolve, reject) => {
                login(user).then(req => {
                    console.log(req.data)
                    storage.saveSessionObject("loginUser", req.data)
                    commit('SAVE_USER_NAME', req.data.user.userName)
                    commit('SAVE_NICK_NAME', req.data.user.nickName)
                    commit('SAVE_TOKEN', req.data.token)
                })
            })

        }
    }
}

export default user
