// 导入创建路由和路由模式的方法
import {createRouter, createWebHistory} from "vue-router";
import {actions, route_white_list, session} from "@/constant";
import store from "@/store";
import storage from "@/utils/storage";
import {checkToken} from "@/api/user";


// 定义路由信息
let routes = [
    {
        name: 'login',
        path: '/login',
        component: () => import('@/components/login')
    },
    {
        name: 'main',
        path: '/main',
        component: () => import('@/components/main')
    }
]

// 创建路由实例并传递 `routes` 配置
// 使用的路由模式是html5的，而不是hash，url中不含#
const router = createRouter({
    history: createWebHistory(),
    routes
})

// 全局的路由守卫，可以在路由进入前做一些工作
router.beforeEach((to, from) => {
    // 获取目的路由名字
    let routeName = to.name
    // 是否需要放行
    if (route_white_list.includes(routeName)) return true

    console.log("执行before")
    // 检查是否登录，登录则放行
    console.log(store.state.user.token)
    if (!store.getters.isLogin) {
        if (storage.sessionKeyIsEmpty(session.SESSION_USER_TAG)) {
            // 尝试从session中恢复数据
            store.dispatch(actions.RESTORE)
        } else {
            router.push({name: 'login'})
            return true
        }
    }
    // 从后端检查token是否合法，如果token不合法，则移除token，跳转到登录也
    // if (!store.getters.isLogin) {
    //     checkToken().then(resp => {
    //         if (resp.status !== 200) {
    //             router.push({name: 'login'})
    //             storage.remove(session.SESSION_USER_TAG)
    //         }
    //     }).catch(error => {
    //         console.log("token不合法")
    //         storage.remove(session.SESSION_USER_TAG)
    //         router.push({name: 'login'})
    //     })
    // }
    // 未登录则登录
    return true
})

// 导出路由实例
export default router
